def min_value(arr):
    menor = arr[0]
    menor_id = 0
    for i in range(1, len(arr)):
      if arr[i] < menor:
          menor = arr[i]
          menor_id = i
    return menor_id


def ordenaca_selecao(arr):
    new_arr = []
    for i in range(len(arr)):
        menor = min_value(arr)
        new_arr.append(arr.pop(menor))
    return new_arr

